import React, { useEffect, useState, useCallback } from 'react';
import { machineService } from '../services/api';
import MachineTable from '../components/MachineTable';

const FILTERS = [
  { label: 'ALL', value: '' },
  { label: 'OPERATIONAL', value: 'operational' },
  { label: 'FAILED', value: 'failed' },
];

const MachineList = ({ onNavigate }) => {
  const [machines, setMachines] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [total, setTotal] = useState(0);
  const LIMIT = 20;

  const fetchMachines = useCallback(async () => {
    setLoading(true);
    try {
      const params = { limit: LIMIT, page, sort: '-timestamp' };
      if (filter) params.status = filter;
      const res = await machineService.getAll(params);
      setMachines(res.data || []);
      setTotalPages(res.pages || 1);
      setTotal(res.total || 0);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [filter, page]);

  useEffect(() => {
    setPage(1);
  }, [filter]);

  useEffect(() => {
    fetchMachines();
  }, [fetchMachines]);

  return (
    <div>
      {/* Header */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1.5rem', flexWrap: 'wrap' }}>
        <button className="btn btn-back" onClick={() => onNavigate('dashboard')}>← DASHBOARD</button>
        <div>
          <h1 style={{ fontFamily: 'var(--font-display)', fontSize: '1.4rem', fontWeight: 700, letterSpacing: '2px' }}>
            MACHINE REGISTRY
          </h1>
          <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.7rem', color: 'var(--text-muted)' }}>
            {total.toLocaleString()} TOTAL RECORDS
          </div>
        </div>
        {/* Filter tabs */}
        <div style={{ marginLeft: 'auto', display: 'flex', gap: '0.5rem' }}>
          {FILTERS.map((f) => (
            <button
              key={f.value}
              onClick={() => setFilter(f.value)}
              style={{
                fontFamily: 'var(--font-mono)', fontSize: '0.7rem', letterSpacing: '1px',
                padding: '0.35rem 0.8rem', border: '1px solid',
                borderColor: filter === f.value ? 'var(--accent-blue)' : 'var(--border)',
                background: filter === f.value ? 'rgba(0,180,255,0.1)' : 'transparent',
                color: filter === f.value ? 'var(--accent-blue)' : 'var(--text-muted)',
                cursor: 'pointer', borderRadius: '2px',
              }}
            >
              {f.label}
            </button>
          ))}
        </div>
      </div>

      {error && <div className="error-box" style={{ marginBottom: '1rem' }}>{error}</div>}

      <div className="card">
        <MachineTable
          machines={machines}
          loading={loading}
          onSelectMachine={(id) => onNavigate('machine-detail', id)}
        />

        {/* Pagination */}
        <div className="pagination">
          <span className="pagination-info">
            PAGE {page} OF {totalPages}
          </span>
          <button className="page-btn" onClick={() => setPage((p) => p - 1)} disabled={page <= 1}>← PREV</button>
          <button className="page-btn" onClick={() => setPage((p) => p + 1)} disabled={page >= totalPages}>NEXT →</button>
        </div>
      </div>
    </div>
  );
};

export default MachineList;
