import React, { useState } from 'react';
import { machineService } from '../services/api';
import HealthIndicator, { HealthBadge } from '../components/HealthIndicator';
import AlertPanel from '../components/AlertPanel';

const DEFAULT_VALUES = {
  machineType: 'M',
  airTemperature: '300.0',
  processTemperature: '310.5',
  rotationalSpeed: '1500',
  torque: '40.0',
  toolWear: '100',
};

const FIELD_META = {
  airTemperature:      { label: 'Air Temperature',      unit: 'K',   min: 290, max: 320, step: 0.1, hint: 'Normal: 295‚Äì304 K' },
  processTemperature:  { label: 'Process Temperature',  unit: 'K',   min: 300, max: 330, step: 0.1, hint: 'Normal: 305‚Äì314 K' },
  rotationalSpeed:     { label: 'Rotational Speed',     unit: 'RPM', min: 500, max: 4000, step: 1,  hint: 'Normal: 1168‚Äì2886 RPM' },
  torque:              { label: 'Torque',                unit: 'Nm',  min: 1,   max: 100,  step: 0.1, hint: 'Normal: 3.8‚Äì76.6 Nm' },
  toolWear:            { label: 'Tool Wear',             unit: 'min', min: 0,   max: 300,  step: 1,  hint: 'Max: 253 min' },
};

const RealtimePredict = ({ onNavigate }) => {
  const [form, setForm] = useState(DEFAULT_VALUES);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (field, value) => {
    setForm((prev) => ({ ...prev, [field]: value }));
    setResult(null);
  };

  const handlePredict = async () => {
    setLoading(true);
    setError(null);
    setResult(null);
    try {
      const payload = {
        machineType: form.machineType,
        airTemperature: parseFloat(form.airTemperature),
        processTemperature: parseFloat(form.processTemperature),
        rotationalSpeed: parseFloat(form.rotationalSpeed),
        torque: parseFloat(form.torque),
        toolWear: parseFloat(form.toolWear),
      };
      const res = await fetch('/api/machines/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      if (!data.success) throw new Error(data.error);
      setResult(data.prediction);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setForm(DEFAULT_VALUES);
    setResult(null);
    setError(null);
  };

  const scoreColor = result
    ? result.healthScore >= 80 ? 'var(--status-good)'
      : result.healthScore >= 60 ? 'var(--status-warning)'
      : result.healthScore >= 40 ? 'var(--status-poor)'
      : 'var(--status-critical)'
    : 'var(--accent-blue)';

  return (
    <div>
      {/* Header */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1.5rem', flexWrap: 'wrap' }}>
        <button className="btn btn-back" onClick={() => onNavigate('dashboard')}>‚Üê DASHBOARD</button>
        <div>
          <h1 style={{ fontFamily: 'var(--font-display)', fontSize: '1.4rem', fontWeight: 700, letterSpacing: '2px' }}>
            REAL-TIME PREDICTION
          </h1>
          <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.7rem', color: 'var(--text-muted)' }}>
            ENTER SENSOR VALUES ‚Üí INSTANT HEALTH &amp; FAILURE PREDICTION
          </div>
        </div>
      </div>

      <div className="grid-2" style={{ alignItems: 'start' }}>
        {/* INPUT FORM */}
        <div className="card">
          <div className="section-title">Sensor Input</div>

          {/* Machine Type */}
          <div style={{ marginBottom: '1.2rem' }}>
            <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.65rem', letterSpacing: '2px', color: 'var(--text-muted)', marginBottom: '0.4rem' }}>
              MACHINE TYPE
            </div>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              {['L', 'M', 'H'].map((t) => (
                <button
                  key={t}
                  onClick={() => handleChange('machineType', t)}
                  style={{
                    flex: 1, padding: '0.5rem', border: '1px solid',
                    borderColor: form.machineType === t ? 'var(--accent-blue)' : 'var(--border)',
                    background: form.machineType === t ? 'rgba(0,180,255,0.12)' : 'transparent',
                    color: form.machineType === t ? 'var(--accent-blue)' : 'var(--text-muted)',
                    fontFamily: 'var(--font-display)', fontWeight: 700, fontSize: '0.9rem',
                    letterSpacing: '2px', cursor: 'pointer', borderRadius: '2px',
                    transition: 'all 0.2s',
                  }}
                >
                  {t === 'L' ? 'LOW' : t === 'M' ? 'MEDIUM' : 'HIGH'}
                </button>
              ))}
            </div>
          </div>

          {/* Sensor Fields */}
          {Object.entries(FIELD_META).map(([field, meta]) => {
            const val = parseFloat(form[field]);
            const pct = Math.min(100, Math.max(0, ((val - meta.min) / (meta.max - meta.min)) * 100));
            return (
              <div key={field} style={{ marginBottom: '1.1rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '0.3rem' }}>
                  <label style={{ fontFamily: 'var(--font-mono)', fontSize: '0.65rem', letterSpacing: '2px', color: 'var(--text-muted)', textTransform: 'uppercase' }}>
                    {meta.label}
                  </label>
                  <span style={{ fontFamily: 'var(--font-mono)', fontSize: '0.75rem', color: 'var(--text-primary)' }}>
                    {form[field]} <span style={{ color: 'var(--text-muted)', fontSize: '0.65rem' }}>{meta.unit}</span>
                  </span>
                </div>
                <input
                  type="range"
                  min={meta.min} max={meta.max} step={meta.step}
                  value={form[field]}
                  onChange={(e) => handleChange(field, e.target.value)}
                  style={{ width: '100%', accentColor: 'var(--accent-blue)', cursor: 'pointer' }}
                />
                <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '0.2rem' }}>
                  <span style={{ fontFamily: 'var(--font-mono)', fontSize: '0.6rem', color: 'var(--text-muted)' }}>{meta.min}</span>
                  <span style={{ fontFamily: 'var(--font-mono)', fontSize: '0.6rem', color: 'var(--accent-blue)', opacity: 0.7 }}>{meta.hint}</span>
                  <span style={{ fontFamily: 'var(--font-mono)', fontSize: '0.6rem', color: 'var(--text-muted)' }}>{meta.max}</span>
                </div>
                {/* Manual number input */}
                <input
                  type="number"
                  min={meta.min} max={meta.max} step={meta.step}
                  value={form[field]}
                  onChange={(e) => handleChange(field, e.target.value)}
                  style={{
                    width: '100%', marginTop: '0.3rem', padding: '0.35rem 0.6rem',
                    background: 'var(--bg-primary)', border: '1px solid var(--border)',
                    color: 'var(--text-primary)', fontFamily: 'var(--font-mono)', fontSize: '0.8rem',
                    borderRadius: '2px', outline: 'none',
                  }}
                  onFocus={(e) => e.target.style.borderColor = 'var(--accent-blue)'}
                  onBlur={(e) => e.target.style.borderColor = 'var(--border)'}
                />
              </div>
            );
          })}

          {/* Buttons */}
          <div style={{ display: 'flex', gap: '0.75rem', marginTop: '1.5rem' }}>
            <button
              onClick={handlePredict}
              disabled={loading}
              style={{
                flex: 2, padding: '0.75rem',
                background: loading ? 'rgba(0,180,255,0.05)' : 'rgba(0,180,255,0.12)',
                border: '1px solid var(--accent-blue)',
                color: 'var(--accent-blue)', fontFamily: 'var(--font-display)',
                fontWeight: 700, fontSize: '0.95rem', letterSpacing: '2px',
                cursor: loading ? 'not-allowed' : 'pointer', borderRadius: '2px',
                transition: 'all 0.2s',
              }}
            >
              {loading ? '‚è≥ ANALYZING...' : '‚ö° PREDICT NOW'}
            </button>
            <button
              onClick={handleReset}
              style={{
                flex: 1, padding: '0.75rem',
                background: 'transparent', border: '1px solid var(--border)',
                color: 'var(--text-muted)', fontFamily: 'var(--font-display)',
                fontWeight: 600, fontSize: '0.85rem', letterSpacing: '1px',
                cursor: 'pointer', borderRadius: '2px',
              }}
            >
              RESET
            </button>
          </div>

          {error && (
            <div className="error-box" style={{ marginTop: '1rem' }}>{error}</div>
          )}
        </div>

        {/* RESULTS PANEL */}
        <div>
          {!result && !loading && (
            <div className="card" style={{ textAlign: 'center', padding: '3rem 2rem' }}>
              <div style={{ fontSize: '3rem', marginBottom: '1rem', opacity: 0.3 }}>‚öô</div>
              <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.75rem', color: 'var(--text-muted)', letterSpacing: '2px' }}>
                ENTER VALUES AND CLICK PREDICT
              </div>
            </div>
          )}

          {loading && (
            <div className="card loading-container" style={{ minHeight: '300px' }}>
              <div className="loading-spinner" />
              <div className="loading-text">RUNNING PREDICTION MODEL...</div>
            </div>
          )}

          {result && (
            <>
              {/* Health Score */}
              <div className="card" style={{ marginBottom: '1rem' }}>
                <div className="section-title">Prediction Result</div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '2rem' }}>
                  <div style={{ position: 'relative', display: 'inline-flex', alignItems: 'center', justifyContent: 'center' }}>
                    <HealthIndicator score={result.healthScore} size="lg" />
                    <div style={{ position: 'absolute', textAlign: 'center' }}>
                      <div style={{ fontFamily: 'var(--font-display)', fontSize: '2.8rem', fontWeight: 700, color: scoreColor, lineHeight: 1 }}>
                        {result.healthScore}
                      </div>
                      <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.55rem', color: 'var(--text-muted)' }}>HEALTH</div>
                    </div>
                  </div>
                  <div style={{ flex: 1 }}>
                    <div style={{ marginBottom: '0.75rem' }}>
                      <HealthBadge status={result.status} />
                    </div>
                    <div className="metric-row">
                      <span className="metric-label">Failure Detected</span>
                      <span className="metric-value" style={{ color: result.failureStatus ? 'var(--status-critical)' : 'var(--status-good)' }}>
                        {result.failureStatus ? '‚ö† YES' : '‚úì NO'}
                      </span>
                    </div>
                    <div className="metric-row">
                      <span className="metric-label">Estimated Power</span>
                      <span className="metric-value">{result.power} W</span>
                    </div>
                    <div className="metric-row">
                      <span className="metric-label">Temp Differential</span>
                      <span className="metric-value">{result.tempDifferential} K</span>
                    </div>
                  </div>
                </div>

                {/* Predicted Failure */}
                <div style={{
                  marginTop: '1rem', padding: '0.75rem 1rem',
                  background: result.failureStatus ? 'rgba(255,34,85,0.08)' : 'rgba(0,255,157,0.06)',
                  border: `1px solid ${result.failureStatus ? 'rgba(255,34,85,0.3)' : 'rgba(0,255,157,0.2)'}`,
                  borderRadius: '2px',
                }}>
                  <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.6rem', letterSpacing: '2px', color: 'var(--text-muted)', marginBottom: '0.25rem' }}>
                    DIAGNOSIS
                  </div>
                  <div style={{ fontFamily: 'var(--font-display)', fontSize: '1rem', fontWeight: 600, color: result.failureStatus ? 'var(--status-critical)' : 'var(--status-good)' }}>
                    {result.predictedFailure}
                  </div>
                </div>

                {/* RUL */}
                <div style={{
                  marginTop: '0.75rem', padding: '0.6rem 1rem',
                  background: 'rgba(0,180,255,0.06)', border: '1px solid rgba(0,180,255,0.2)', borderRadius: '2px',
                }}>
                  <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.6rem', letterSpacing: '2px', color: 'var(--text-muted)', marginBottom: '0.2rem' }}>
                    REMAINING USEFUL LIFE
                  </div>
                  <div style={{ fontFamily: 'var(--font-display)', fontSize: '0.95rem', color: 'var(--accent-cyan)' }}>
                    {result.rulEstimate}
                  </div>
                </div>
              </div>

              {/* Alerts */}
              <div className="card" style={{ marginBottom: '1rem' }}>
                <div className="section-title">Detected Anomalies ({result.alerts.length})</div>
                <AlertPanel alerts={result.alerts} />
              </div>

              {/* Recommendation */}
              <div className="card">
                <div className="section-title">Maintenance Recommendation</div>
                <div style={{ fontFamily: 'var(--font-body)', fontSize: '0.85rem', color: 'var(--text-secondary)', lineHeight: 1.6 }}>
                  {result.healthScore >= 80 && '‚úÖ Machine is in good health. Continue normal operation and schedule routine inspection.'}
                  {result.healthScore >= 60 && result.healthScore < 80 && '‚ö†Ô∏è Minor issues detected. Monitor closely and plan preventive maintenance within the next maintenance window.'}
                  {result.healthScore >= 40 && result.healthScore < 60 && 'üî∂ Significant degradation detected. Schedule maintenance soon. Reduce load if possible.'}
                  {result.healthScore < 40 && 'üö® Critical condition. Immediate maintenance required. Consider stopping the machine to prevent damage.'}
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default RealtimePredict;
