import React from 'react';

const HealthIndicator = ({ score, size = 'md' }) => {
  const getColor = (s) => {
    if (s >= 80) return '#00ff9d';
    if (s >= 60) return '#ffd600';
    if (s >= 40) return '#ff8c00';
    return '#ff2255';
  };

  const color = getColor(score);
  const radius = size === 'lg' ? 54 : 34;
  const stroke = size === 'lg' ? 5 : 4;
  const dim = (radius + stroke) * 2;
  const circumference = 2 * Math.PI * radius;
  const progress = circumference - (score / 100) * circumference;

  return (
    <svg width={dim} height={dim} style={{ transform: 'rotate(-90deg)' }}>
      <circle cx={dim / 2} cy={dim / 2} r={radius} fill="none" stroke="rgba(30,52,72,0.8)" strokeWidth={stroke} />
      <circle
        cx={dim / 2} cy={dim / 2} r={radius}
        fill="none"
        stroke={color}
        strokeWidth={stroke}
        strokeDasharray={circumference}
        strokeDashoffset={progress}
        strokeLinecap="round"
        style={{ filter: `drop-shadow(0 0 4px ${color})`, transition: 'stroke-dashoffset 0.6s ease' }}
      />
    </svg>
  );
};

export const HealthBadge = ({ status }) => (
  <span className={`health-badge ${status}`}>{status}</span>
);

export default HealthIndicator;
