import React, { useEffect, useState, useCallback } from 'react';
import { machineService } from '../services/api';
import { StatusDoughnut, HealthTrendChart, ToolWearChart } from '../components/ChartsPanel';
import MachineTable from '../components/MachineTable';
import AlertPanel from '../components/AlertPanel';

const Dashboard = ({ onNavigate }) => {
  const [summary, setSummary] = useState(null);
  const [machines, setMachines] = useState([]);
  const [allAlerts, setAllAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = useCallback(async () => {
    try {
      setError(null);
      const [summaryRes, machinesRes] = await Promise.all([
        machineService.getSummary(),
        machineService.getAll({ limit: 10, sort: '-timestamp' }),
      ]);

      setSummary(summaryRes);
      setMachines(machinesRes.data || []);

      // Collect all active alerts from recent machines
      const alerts = [];
      (machinesRes.data || []).forEach((m) => {
        (m.alerts || []).forEach((a) => alerts.push({ ...a, machineId: m.machineId }));
      });
      setAllAlerts(alerts.slice(0, 8));
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 30000); // refresh every 30s
    return () => clearInterval(interval);
  }, [fetchData]);

  if (loading) return (
    <div className="loading-container" style={{ minHeight: '60vh' }}>
      <div className="loading-spinner" />
      <div className="loading-text">INITIALIZING DASHBOARD...</div>
    </div>
  );

  if (error) return (
    <div style={{ padding: '2rem' }}>
      <div className="error-box">
        <strong>API Connection Error:</strong> {error}
        <br /><br />
        <span style={{ fontSize: '0.8rem', opacity: 0.7 }}>
          Make sure the backend is running on port 5000 and MongoDB is connected.
        </span>
      </div>
    </div>
  );

  const s = summary?.summary || {};
  const avg = summary?.avgStats || {};

  return (
    <div>
      {/* Page Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
        <div>
          <h1 style={{ fontFamily: 'var(--font-display)', fontSize: '1.6rem', fontWeight: 700, letterSpacing: '2px', color: 'var(--text-primary)' }}>
            OPERATIONS OVERVIEW
          </h1>
          <div style={{ fontFamily: 'var(--font-mono)', fontSize: '0.7rem', color: 'var(--text-muted)', marginTop: '0.2rem' }}>
            INDUSTRIAL EQUIPMENT MONITORING · AI4I DATASET
          </div>
        </div>
        <button className="btn" onClick={() => onNavigate('machines')}>VIEW ALL MACHINES →</button>
      </div>

      {/* Stat Cards */}
      <div className="grid-stats">
        <div className="card stat-card blue">
          <div className="stat-label">Total Machines</div>
          <div className="stat-value">{s.total?.toLocaleString() || '—'}</div>
          <div className="stat-sub">in dataset</div>
        </div>
        <div className="card stat-card good">
          <div className="stat-label">Operational</div>
          <div className="stat-value">{s.operational?.toLocaleString() || '—'}</div>
          <div className="stat-sub">{s.total ? `${(100 - s.failureRate).toFixed(1)}% uptime` : ''}</div>
        </div>
        <div className="card stat-card critical">
          <div className="stat-label">Failures</div>
          <div className="stat-value">{s.failed?.toLocaleString() || '—'}</div>
          <div className="stat-sub">{s.failureRate ? `${s.failureRate}% failure rate` : ''}</div>
        </div>
        <div className="card stat-card warning">
          <div className="stat-label">Avg Health Score</div>
          <div className="stat-value">{s.averageHealthScore || '—'}</div>
          <div className="stat-sub">out of 100</div>
        </div>
        <div className="card stat-card">
          <div className="stat-label">Avg Tool Wear</div>
          <div className="stat-value" style={{ fontSize: '1.6rem', color: 'var(--accent-blue)' }}>
            {avg.avgToolWear ? Math.round(avg.avgToolWear) : '—'}
          </div>
          <div className="stat-sub">minutes</div>
        </div>
        <div className="card stat-card">
          <div className="stat-label">Avg Torque</div>
          <div className="stat-value" style={{ fontSize: '1.6rem', color: 'var(--accent-cyan)' }}>
            {avg.avgTorque ? avg.avgTorque.toFixed(1) : '—'}
          </div>
          <div className="stat-sub">Nm</div>
        </div>
      </div>

      {/* Charts Row */}
      <div className="grid-3" style={{ marginBottom: '1.5rem' }}>
        <div className="card">
          <div className="section-title">Status Breakdown</div>
          <StatusDoughnut breakdown={s.statusBreakdown || {}} />
        </div>
        <div className="card">
          <div className="section-title">Health Score Trend</div>
          <HealthTrendChart />
        </div>
        <div className="card">
          <div className="section-title">Tool Wear Distribution</div>
          <ToolWearChart machines={machines} />
        </div>
      </div>

      {/* Recent Machines + Alerts */}
      <div className="grid-2">
        <div className="card">
          <div className="section-title">Recent Machine Readings</div>
          <MachineTable
            machines={machines}
            onSelectMachine={(id) => onNavigate('machine-detail', id)}
          />
        </div>
        <div>
          <div className="card" style={{ marginBottom: '1rem' }}>
            <div className="section-title">Active Alerts</div>
            <AlertPanel alerts={allAlerts} />
          </div>
          {summary?.failureByType && (
            <div className="card">
              <div className="section-title">Failure Type Breakdown</div>
              {summary.failureByType.filter(f => f._id !== 'No Failure').map((f, i) => (
                <div key={i} className="metric-row">
                  <span className="metric-label">{f._id}</span>
                  <span className="metric-value" style={{ color: 'var(--status-critical)' }}>{f.count}</span>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
