import React, { useEffect, useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale, LinearScale, PointElement, LineElement,
  BarElement, ArcElement, Title, Tooltip, Legend, Filler,
} from 'chart.js';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import { machineService } from '../services/api';

ChartJS.register(
  CategoryScale, LinearScale, PointElement, LineElement,
  BarElement, ArcElement, Title, Tooltip, Legend, Filler
);

const chartDefaults = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: {
      labels: { color: '#7ba5c9', font: { family: 'Share Tech Mono', size: 11 }, boxWidth: 10 },
    },
    tooltip: {
      backgroundColor: '#111924',
      borderColor: '#1e3448',
      borderWidth: 1,
      titleColor: '#e8f4ff',
      bodyColor: '#7ba5c9',
    },
  },
  scales: {
    x: {
      ticks: { color: '#3d6080', font: { family: 'Share Tech Mono', size: 10 } },
      grid: { color: 'rgba(30,52,72,0.4)' },
    },
    y: {
      ticks: { color: '#3d6080', font: { family: 'Share Tech Mono', size: 10 } },
      grid: { color: 'rgba(30,52,72,0.4)' },
    },
  },
};

// Tool Wear Distribution Bar Chart
export const ToolWearChart = ({ machines = [] }) => {
  const buckets = [0, 0, 0, 0, 0]; // 0-50, 51-100, 101-150, 151-200, 200+
  machines.forEach(({ toolWear }) => {
    if (toolWear <= 50) buckets[0]++;
    else if (toolWear <= 100) buckets[1]++;
    else if (toolWear <= 150) buckets[2]++;
    else if (toolWear <= 200) buckets[3]++;
    else buckets[4]++;
  });

  const data = {
    labels: ['0–50', '51–100', '101–150', '151–200', '200+'],
    datasets: [{
      label: 'Machines',
      data: buckets,
      backgroundColor: ['#00ff9d33', '#ffd60033', '#ff8c0033', '#ff8c0066', '#ff225566'],
      borderColor: ['#00ff9d', '#ffd600', '#ff8c00', '#ff8c00', '#ff2255'],
      borderWidth: 1,
    }],
  };

  return (
    <div style={{ height: 200 }}>
      <Bar data={data} options={{ ...chartDefaults, plugins: { ...chartDefaults.plugins, title: { display: false } } }} />
    </div>
  );
};

// Status Doughnut
export const StatusDoughnut = ({ breakdown = {} }) => {
  const { GOOD = 0, WARNING = 0, POOR = 0, CRITICAL = 0 } = breakdown;
  const data = {
    labels: ['Good', 'Warning', 'Poor', 'Critical'],
    datasets: [{
      data: [GOOD, WARNING, POOR, CRITICAL],
      backgroundColor: ['rgba(0,255,157,0.7)', 'rgba(255,214,0,0.7)', 'rgba(255,140,0,0.7)', 'rgba(255,34,85,0.7)'],
      borderColor: ['#00ff9d', '#ffd600', '#ff8c00', '#ff2255'],
      borderWidth: 1,
    }],
  };

  const opts = {
    responsive: true, maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'right',
        labels: { color: '#7ba5c9', font: { family: 'Share Tech Mono', size: 10 }, boxWidth: 10, padding: 8 },
      },
      tooltip: chartDefaults.plugins.tooltip,
    },
    cutout: '68%',
  };

  return <div style={{ height: 180 }}><Doughnut data={data} options={opts} /></div>;
};

// Health Score Trend Line
export const HealthTrendChart = () => {
  const [trendData, setTrendData] = useState(null);

  useEffect(() => {
    machineService.getTrends({ metric: 'healthScore', limit: 40 })
      .then((res) => setTrendData(res.data))
      .catch(() => {});
  }, []);

  if (!trendData) return <div style={{ height: 200, display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'var(--text-muted)', fontFamily: 'var(--font-mono)', fontSize: '0.7rem' }}>LOADING...</div>;

  const makeDataset = (type, color) => ({
    label: `Type ${type}`,
    data: (trendData[type] || []).map((p) => p.y),
    borderColor: color,
    backgroundColor: color + '15',
    fill: true,
    tension: 0.4,
    pointRadius: 2,
    borderWidth: 1.5,
  });

  const maxLen = Math.max(...Object.values(trendData).map((d) => d.length));
  const labels = Array.from({ length: maxLen }, (_, i) => `#${maxLen - i}`).reverse();

  const data = {
    labels,
    datasets: [
      makeDataset('H', '#00ff9d'),
      makeDataset('M', '#00b4ff'),
      makeDataset('L', '#ffd600'),
    ],
  };

  return (
    <div style={{ height: 200 }}>
      <Line data={data} options={chartDefaults} />
    </div>
  );
};

// Machine Parameter Radar-style Bar
export const ParameterChart = ({ machine }) => {
  if (!machine) return null;

  const data = {
    labels: ['Air Temp (K)', 'Process Temp (K)', 'Speed (rpm/10)', 'Torque (Nm)', 'Tool Wear (min)'],
    datasets: [
      {
        label: 'Current Value',
        data: [
          machine.airTemperature,
          machine.processTemperature,
          machine.rotationalSpeed / 10,
          machine.torque,
          machine.toolWear,
        ],
        backgroundColor: 'rgba(0,180,255,0.15)',
        borderColor: '#00b4ff',
        borderWidth: 1.5,
        pointBackgroundColor: '#00e5ff',
        pointRadius: 3,
      },
    ],
  };

  return (
    <div style={{ height: 200 }}>
      <Bar data={data} options={chartDefaults} />
    </div>
  );
};
